// ==UserScript==
// @name         FreelanceJP
// @namespace    https://github.com/w-masahiro-ct/tampermonkey-userscripts
// @version      1.0
// @description  Click my page menu button.
// @author       M
// @match        https://www.freelance-jp.org/mypage
// @icon         data:image/png;base64,
// @grant        none
// @updateURL    https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/freelance-jp.user.js
// @downloadURL  https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/freelance-jp.user.js
// @supportURL   https://github.com/w-masahiro-ct/tampermonkey-userscripts
// ==/UserScript==

(() => {
  'use strict';
  const freelanceJp = () => {
    const task = () => {
      const myPageMenuButton = document.querySelector('#my-page-menu-btn');
      const myPageMenuContainer = document.querySelector('#my-page-menu-container');
      if (myPageMenuButton && myPageMenuContainer && window.getComputedStyle(myPageMenuContainer).display === 'none') {
        myPageMenuButton.click();
      }
    };
    setTimeout(task, 10);
    setTimeout(task, 50);
    setTimeout(task, 100);
    setTimeout(task, 250);
    setTimeout(task, 500);
    setTimeout(task, 1000);
    setTimeout(task, 5000);
  };
  freelanceJp();
  const observer = new MutationObserver(freelanceJp);
  observer.observe(document, { childList: true, subtree: true });
  window.addEventListener('popstate', freelanceJp);
})();
