// ==UserScript==
// @name         KBPT
// @namespace    https://github.com/w-masahiro-ct/tampermonkey-userscripts
// @version      1.0
// @description  Show availability of stock.
// @author       M
// @match        https://kouboupath.base.ec/*
// @icon         data:image/png;base64,
// @grant        none
// @updateURL    https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/kbpt.user.js
// @downloadURL  https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/kbpt.user.js
// @supportURL   https://github.com/w-masahiro-ct/tampermonkey-userscripts
// ==/UserScript==

(() => {
  'use strict';
  const kbpt = () => {
    const task = () => {
      const infoContainers = document.querySelectorAll('[class^="items-grid_infoContainer"]');
      for (const infoContainer of infoContainers) {
        infoContainer.style.opacity = 0.8;
      }
    };
    setTimeout(task, 10);
    setTimeout(task, 25);
    setTimeout(task, 50);
    setTimeout(task, 100);
    setTimeout(task, 250);
    setTimeout(task, 500);
    setTimeout(task, 1000);
    setTimeout(task, 5000);
    setTimeout(task, 10000);
  };
  kbpt();
  const observer = new MutationObserver(kbpt);
  observer.observe(document, { childList: true, subtree: true });
  window.addEventListener('popstate', kbpt);
})();
