// ==UserScript==
// @name         RakutenLoginButton
// @namespace    https://github.com/w-masahiro-ct/tampermonkey-userscripts
// @version      1.0
// @description  Enlarge the login button.
// @author       M
// @match        *://www.rakuten-sec.co.jp/*
// @match        *://*.id.rakuten.co.jp/*
// @match        *://www.rakuten-card.co.jp/*
// @icon         data:image/png;base64,
// @grant        none
// @updateURL    https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/rakuten-login-button.user.js
// @downloadURL  https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/rakuten-login-button.user.js
// @supportURL   https://github.com/w-masahiro-ct/tampermonkey-userscripts
// ==/UserScript==

(() => {
  'use strict';
  const rakutenLoginButton = () => {
    const task = () => {
      if (location.hostname === 'www.rakuten-sec.co.jp') {
        const button = document.querySelector('#login-btn');
        if (button == null) {
          return;
        }

        button.style.cssText = 'padding: 100px 0;';
        return;
      }

      const button = document.querySelector('.loginButton');
      if (button == null) {
        return;
      }

      button.style.minHeight = '350px';
      button.style.minWidth = '350px';
    };
    setTimeout(task, 10);
    setTimeout(task, 50);
    setTimeout(task, 100);
    setTimeout(task, 250);
    setTimeout(task, 500);
    setTimeout(task, 1000);
    setTimeout(task, 5000);
  };
  rakutenLoginButton();
})();
