// ==UserScript==
// @name         ReleasePR
// @namespace    https://github.com/w-masahiro-ct/tampermonkey-userscripts
// @version      1.0
// @description  Disable merge button of release PR.
// @author       M
// @match        https://github.com/*
// @icon         data:image/png;base64,
// @grant        none
// @updateURL    https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/release-pr.user.js
// @downloadURL  https://github.com/w-masahiro-ct/tampermonkey-userscripts/raw/main/src/release-pr.user.js
// @supportURL   https://github.com/w-masahiro-ct/tampermonkey-userscripts
// ==/UserScript==

(() => {
  'use strict';
  const releasePr = () => {
    const task = () => {
      const h1Tags = document.querySelectorAll('h1');
      const filteredH1Tags = Array.from(h1Tags).filter(h1Tag => h1Tag.innerText.startsWith('Staging Release') || h1Tag.innerText.startsWith('Main Release'));
      const mergeButton = document.querySelector('.merge-box-button.btn-group-merge');
      if (filteredH1Tags.length > 0 && mergeButton) {
        mergeButton.disabled = true;
      }
    };
    setTimeout(task, 10);
    setTimeout(task, 25);
    setTimeout(task, 50);
    setTimeout(task, 100);
    setTimeout(task, 250);
    setTimeout(task, 500);
    setTimeout(task, 1000);
    setTimeout(task, 5000);
    setTimeout(task, 10000);
  };
  releasePr();
  const observer = new MutationObserver(releasePr);
  observer.observe(document, { childList: true, subtree: true });
  window.addEventListener('popstate', releasePr);
})();
